script:
frappe.ui.form.on('Quotation', {
	refresh(frm) {
		cur_frm.fields_dict["items"].grid.wrapper.find(".grid-remove-rows").hide();

		cur_frm.fields_dict["items"].grid.wrapper.find(".grid-delete-row").hide();
	},
	items_package:function(frm){
	    cur_frm.clear_table("items")
        refresh_field("items");
	    frappe.db.get_list('Items', {
	        parent:"Items Package",
	        filters:{"parent": frm.doc.items_package},
            fields:["item_code","qty","rate"]
		}).then(r => {
			if(r){
			    let d = "";
			    r.forEach(function(item) {
                    let d = frappe.model.add_child(frm.doc, "Quotation Item", "items");
                    d.item_code = item.item_code;
                    d.qty = item.qty;
                    d.rate = item.rate;
                    cur_frm.script_manager.trigger("item_code", d.doctype, d.name);
                });
			}
            refresh_field("items");
		});
	}
})

frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		console.log("===============Sales Invoice====================");
	},
	contract_details_: function(frm){
	   ["posting_date","contract_deale_type_","contract_summary","contract_duration"].forEach(function(key){
        if(key in cur_frm.doc)
            cur_frm.doc.contract_details_ = cur_frm.doc.contract_details_.split("{"+key+"}").join(cur_frm.doc[key]);
        });
        cur_frm.refresh_field("contract_details_");
	},
	contract_type: function(frm){
		frappe.db.get_value("Contract Type", cur_frm.doc.contract_type, "terms", (r) => {
			if(r && 'terms' in r)
			    cur_frm.doc.contract_details_ = r.terms;
    		cur_frm.trigger("contract_details_");

		});
	}
})
