script:
frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		console.log("===============Sales Invoice====================");
	},
	contract_details_: function(frm){
	   ["posting_date","contract_deale_type_","contract_summary","contract_duration"].forEach(function(key){
        if(key in cur_frm.doc)
            cur_frm.doc.contract_details_ = cur_frm.doc.contract_details_.split("{"+key+"}").join(cur_frm.doc[key]);
        });
        cur_frm.refresh_field("contract_details_");
	},
	contract_type: function(frm){
		frappe.db.get_value("Contract Type", cur_frm.doc.contract_type, "terms", (r) => {
			if(r && 'terms' in r)
			    cur_frm.doc.contract_details_ = r.terms;
    		cur_frm.trigger("contract_details_");

		});
	}
})
